mapper.select:
select a.*, b.ai_name as author
from book_info a left outer join author_info b on a.bi_ai_seq=b.ai_seq

    where bi_name like #{keyword} or b.ai_name like #{keyword}


    where bi_name like #{keyword}


    where b.ai_name like #{keyword}

order by bi_seq desc
limit 10 offset #{offset}

select count(*)
from book_info a left outer join author_info b on a.bi_ai_seq=b.ai_seq

    where bi_name like #{keyword} or b.ai_name like #{keyword}


    where bi_name like #{keyword}


    where b.ai_name like #{keyword}

select a.*, b.ai_name as author, b.ai_number as author_code
from book_info a left outer join author_info b on a.bi_ai_seq=b.ai_seq
where a.bi_seq=#{seq}

mapper.update:
update book_info
set bi_name=#{bi_name}, bi_number=#{bi_number}, bi_status=#{bi_status}, bi_ai_seq=#{bi_ai_seq}, bi_mod_dt=NOW()
where bi_seq=#{bi_seq}
